'use strict';
var WebClient = require('../');

var move = function() {
    let num = 100;
    while (num--) {
        let url = num == 30 ? 'https://www.google.com' : 'https://db.oklahoma.me';
        WebClient.get({
            url: url,
            timeout: 2000
        }).then(function(result) {
            let response = result[0], body = result[1];
            response.body = body.length;
            // console.log(response);

            // move();
            console.log(response);
        }).catch(function (err) {
            // if (url == 'https://www.google.com')
                console.log(err[0].stack.toString(), err[0].url);
        });
    }
};

move();
